input checked events.back:
/* any "checkable" element */
:checked {
  width: 50px;
  height: 50px;
}

/* only radio elements */
  input[type="radio"]:checked {
  margin-left: 25px;
}

/* only checkbox elements */
input[type="checkbox"]:checked {
  display: none;
}

/* only option elements */
option:checked {
  color: red;
}
